package operators;

public class BitwiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5,b=6;
		int c=a&b;
		int d=a|b;
		int e=b>>1;
		int f=b<<1;
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
		System.out.println(f);



		

	}

}
